import * as actionTypes from "../constants/action-types";


export const addToCompare = (product) => {

    return async (dispatch) => {
        try {

            const response = await fetch(`http://localhost:3000/products/${product}`);
            const productData = await response.json();

            dispatch({
                type: actionTypes.ADD_TO_COMPARE,
                payload: productData,
            });
        } catch (error) {
            console.error('Error fetching product details:', error);

        }
    };

}


export const removeFromCompare = (product) => {

    return async (dispatch) => {
        try {

            const response = await fetch(`http://localhost:3000/products/${product}`);
            const productData = await response.json();


            dispatch({
                type: actionTypes.REMOVE_FROM_COMPARE,
                payload: product,
            });
        } catch (error) {
            console.error('Error fetching product details:', error);

        }
    };

}
