import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from 'react-redux';
import actions from "../actions";
import { getImage } from './server'; // Import the getImage function
import ProductCard from "./ProductCard";
import Product from "./product";


export default function Compare() {
    const [product, setProducts] = useState([]);
    const [ image, setImages] = useState([]);
    const dispatch = useDispatch();
     const products = useSelector((state) => state.compare.products);
    //console.log(products);

    //const ProductCard = useSelector((state) => state.catalog.products);
    //console.log(ProductCard);
    
    //run on first render
    useEffect(() => {
       // Dispatch the initCatalog() action creator to fetch products
        dispatch(actions.addToCompare());
    }, [dispatch]);
    
   const getProducts = (id) => {
    fetch(`/api/Products/${id}`)
    .then((res) => res.json())
    .then((data) => {
        setProducts(data.products);
    })
    .catch((err) => console.log("Products not found"));
   }

   const getImage = (id) => {
    fetch(`/api/Images/${id}`)
    .then((res) => res.json())
    .then((data) => {
        setImages(data.Images);
    })
    .catch((err) => console.log("Products not found"));
    // Function to handle the "Remove" button click
    const handleRemoveClick = (ProductCard) => {
        if (window.confirm("Are you sure to delete this product")) {
            // Dispatch the removeFromCompare() action creator with the productId
            dispatch(actions.removeFromCompare(ProductCard.id));
        }
    };

    return (
        <>
            {
                products.map((ProductCard) => (

                    < div className="comparison-grid" key={ProductCard.id}>

                        <div className="feature-labels">
                            <div>Brand</div>
                            <div>Model</div>
                            <div>Rear Camera</div>
                            <div>Front Camera</div>
                            <div>Screen</div>
                            <div>Storage</div>
                            <div>OS</div>
                            <div>Cost</div>
                        </div>
                        <div className="product-table">
                            <div className="product-features">
                                <div>
                                    <img src={getImage(ProductCard.image)}
                                        alt={ProductCard.model} />
                                </div>
                                <div>{ProductCard.brand}</div>
                                <div>{ProductCard.model}</div>
                                <div>{ProductCard.rear_camera}</div>
                                <div>{ProductCard.front_camera}</div>
                                <div>{ProductCard.screen}</div>
                                <div>{ProductCard.storage}</div>
                                <div>{ProductCard.os}</div>
                                <div>{ProductCard.cost}</div>
                                <div>
                                    <button onClick={() => handleRemoveClick(ProductCard)}>REMOVE</button>
                                </div>
                            </div>
                        </div>



                    </div >
                ))};
        </>
    );
};
}
