import { initCatalog, productDetails } from './actions/catalogAction';

const initialState = {
    products: [],
    selected: null,
};

const catalogReducer = (state = initialState, action) => {
    switch(action.type) {
        case "INITIALIZE_CATALOG":
            return{
                ...state,
                products: action.payload,
                selected: null,
            };
            case "PRODUCT_DETAILS":
                return {
                    ...state,
                    selected: action.payload,
                };
                default:
                    return state;
    }
};
export default catalogReducer;
