import React from "react";
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCompare } from '../store/actions/compareActions'; // Import your action creator
import { getImage } from '../server'; // Import the getImage function


const Compare = () => {
  const dispatch = useDispatch();
  const comparedProducts = useSelector((state) => state.compare.products);

  // Function to handle the "Remove" button click
  const handleRemoveClick = (productId) => {
    // Dispatch the removeFromCompare() action creator with the productId
    dispatch(removeFromCompare(productId));
  };

  return (
    <div className="comparison-grid">
      <div className="feature-labels">
        <div></div>
        <div>Brand</div>
        <div>Model</div>
        <div>Rear Camera</div>
        <div>Front Camera</div>
        <div>Screen</div>
        <div>Storage</div>
        <div>OS</div>
        <div>Cost</div>
      </div>
      <div className="product-table">
        <div className="product-features">
          <div>
            <img src={getImage(product.image)}
            alt={product.model} />
          </div>
          <div>{product.brand}</div>
          <div>{product.model}</div>
          <div>{product.rear_camera}</div>
          <div>{product.front_camera}</div>
          <div>{product.screen}</div>
          <div>{product.storage}</div>
          <div>{product.os}</div>
          <div>{product.cost}</div>
          <div>
            <button onClick={() => handleRemoveClick(product)}>REMOVE</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Compare;
